import { useState, useEffect } from "react";
import axios from "axios";
import { useRouter } from "next/router";

const Describtion = () => {
  const [id, setId] = useState(null);
  const [product, setProduct] = useState({});
  const [cartItems, setCartItems] = useState([]);

  const router = useRouter();

  useEffect(() => {
    setId(router.query.productId);
  }, [router.query.productId]);

  useEffect(() => {
    if (id) {
      fetchProduct(id);
    }
  }, [id]);

  const fetchProduct = async (id) => {
    try {
      const response = await axios.get(
        `http://localhost:9000/product/Product/${id}/`,
        {
          headers: { authorization: localStorage.getItem("token") },
        },
      );
      setProduct(response.data.data.product);
    } catch (error) {
      console.error("Error fetching product data:", error);
    }
  };

  const addToCart = async () => {
    if (!localStorage.getItem("token")) {
      return alert("user not found");
    }
    
    try {
      const response = await axios.patch(
        "http://localhost:9000/cart/add",
        {
          productId: product._id,
          quantity: 1,
        },
        {
          headers: { authorization: localStorage.getItem("token") },
        },
      );

      setCartItems(response.data.cartItems);
      console.log("Product added to cart successfully");
    } catch (error) {
      console.error("Error adding product to cart:", error);
    }
  };

  return (
    <>
      <div className="lg:px-72 lg:grid grid-cols-2 gap-y-3">
        <div>
          {product?.images && product?.images.length > 0 ? (
            <img
              src={product?.images[0]?.url}
              className="w-full h-auto lg:w-full lg:h-[600px] p-6 pb-2"
              alt={product?.name}
            />
          ) : (
            ""
          )}
        </div>
        <div>
          <p className="text-3xl">{product.name}</p>
          <p className="text-sm">{product.sku_id}</p>
          <p className="mt-2 text-orange-500 text-lg mb-3">${product.price}</p>
          {/* <select className="px-4 py-2 border border-gray-300 rounded-md">
             <option value="small">{product.sizes[0]}</option>
             <option value="medium">{product.sizes[1]}</option>
             <option value="large">{product.sizes[2]}</option>
           </select>  */}
          <p className="px-4 py-3">Size</p>
          <div className="flex items-center py-1 px-3 ">
            <select className="px-8 py-2 border border-gray-300 rounded-md">
              <option value="small">S</option>
              <option value="medium">M</option>
              <option value="large">L</option>
            </select>
          </div>

          <p className="px-4 py-3">Color</p>

          <div className="flex items-center  px-4 py-2">
            <input
              type="radio"
              id="option1"
              name="radio"
              className="appearance-none w-4 h-4 bg-orange-500 rounded-full"
            />
            <label htmlFor="option1" className="ml-2 text-orange-500"></label>
            <input
              type="radio"
              id="option2"
              name="radio"
              className="appearance-none w-4 h-4 bg-black rounded-full ml-1"
            />
            <label htmlFor="option2" className="ml-2 "></label>
          </div>

          <p className="px-4 py-3">Quantity</p>
          <div className="flex items-center mt-2 px-4">
            <input
              type="text"
              className="h-10 w-14 px-0 py-2 border border-gray-300 text-black"
              min="1"
            />
          </div>
          <br></br>
          <button
            onClick={addToCart}
            className="bg-transparent hover:bg-orange-500 bg-orange-500 font-semibold hover:text-white py-2 px-4  border-orange-500 hover:border-solid border-2 text-orange-500"
          >
            Add to Cart
          </button>
          <br></br>
          <button className="bg-black mt-2 hover:bg-orange-500 font-semibold text-white py-2 px-4 hover:border-solid border-2 text-orange-500">
            Buy Now
          </button>
        </div>
      </div>

      {/* Related products section */}
    </>
  );
};

export default Describtion;
